import React, { useState, useEffect } from 'react';
import './Activities.css'; // Import your CSS file
import { PiBowlFoodLight } from 'react-icons/pi'; // Import icons
const Activities = () => {
  const [activityTypes, setActivityTypes] = useState(['Physical Activities', 'Important Tasks', 'Medicine', 'Calorie Counter']);
  const [selectedActivity, setSelectedActivity] = useState(localStorage.getItem('selectedActivity') || ''); // Initialize from local storage
  const [activities, setActivities] = useState(JSON.parse(localStorage.getItem('activities')) || []); // Initialize from local storage
  const [activityName, setActivityName] = useState('');
  const [activityTime, setActivityTime] = useState('');
  const [reminder, setReminder] = useState(false);

  const selectActivityType = (type) => {
    setSelectedActivity(type);
  };
  const activityInfo = {
    'Physical Activities': {
     
      image: "data:image/jpeg;base64,",
    },
    'Important Tasks': {
      icon: <PiBowlFoodLight/>,
      image: 'data:image/jpeg;base64,',
    },
    'Medicine': {
      icon: <PiBowlFoodLight/>,
      image: 'data:image/png;base64,',
    },
    'Calorie Counter': {
      icon: <PiBowlFoodLight/>,
      image: 'data:image/png;base64,',
    },
  };
  
  const addActivity = () => {
    if (!activityName || !activityTime || !selectedActivity) {
      alert('Please enter activity type, name, and time.');
      return;
    }

    const newActivity = {
      type: selectedActivity,
      name: activityName,
      time: activityTime,
      reminder,
    };

    setActivities([...activities, newActivity]);
    setActivityName('');
    setActivityTime('');
    setReminder(false);
  };

  const deleteActivity = (index) => {
    const updatedActivities = [...activities];
    updatedActivities.splice(index, 1);
    setActivities(updatedActivities);
  };

  useEffect(() => {
    localStorage.setItem('activities', JSON.stringify(activities));
    localStorage.setItem('selectedActivity', selectedActivity);
  }, [activities, selectedActivity]);

  const filteredActivities = selectedActivity
    ? activities.filter((activity) => activity.type === selectedActivity)
    : activities;

    return (
      <div className='activity'>
        <h2>Activities Tracker</h2>
        <div>
          <h3>Select Activity Type:</h3>
          <div className="activity-types">
          {activityTypes.map((type) => (
            <div
              key={type}
              className={`activity-type ${selectedActivity === type ? 'selected' : ''}`}
              onClick={() => selectActivityType(type)}
            >
             
              <img className='pic' src={activityInfo[type].image} alt={type} /> {/* Use the image */}
              
            </div>
          ))}
          </div>
        </div>
        {selectedActivity && (
          <div className='activitydesc'>
            <div className='inputtext'>
              <input 
                type="text"
                placeholder="Activity Name"
                value={activityName}
                onChange={(e) => setActivityName(e.target.value)}
              />
            
              <input
                className='clock'
                type="time"
                
                value={activityTime}
                onChange={(e) => setActivityTime(e.target.value)}
              />
              <label className='clock-label'>‚è∞ Select Time</label>
          
            
              
            </div>
            <label>
              Set Reminder:
              <input className='check'
                type="checkbox"
                checked={reminder}
                onChange={() => setReminder(!reminder)}
              />
            </label>
            <button onClick={addActivity}>Add Activity</button>
          </div>
        )}
        <ul>
          {filteredActivities.map((activity, index) => (
            <li key={index}>
              {activity.type}: {activity.name} at {activity.time}
              {activity.reminder && <span> - Reminder Set</span>}
              <button onClick={() => deleteActivity(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    );
};

export default Activities;
